/* Hero section global background gradient utility */
.bg-hero-gradient {
  background: linear-gradient(135deg, var(--primary) 0%, var(--accent) 80%, var(--background) 100%);
}
.dark .bg-hero-gradient {
  background: linear-gradient(135deg, var(--primary) 0%, var(--accent) 80%, var(--background) 100%);
}

/* Minimal fade-in/slide-up animation utility */
@keyframes fade-in-up {
  0% {
    opacity: 0;
    transform: translateY(24px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
}

.animate-fade-in-up {
  animation: fade-in-up 0.7s cubic-bezier(0.22,0.61,0.36,1) both;
}
