old_text:
import Image from "next/image"

interface HeroLinkProps {
  href: string
  imageSrc: string
  imageAlt: string
  children: React.ReactNode
}

export function HeroLink({ href, imageSrc, imageAlt, children }: HeroLinkProps) {
  return (
    <a 
      href={href} 
      target="_blank" 
      rel="noopener noreferrer" 
      className="inline-flex items-center gap-1.5 text-primary hover:text-primary/80 rounded px-1.5 py-0.5 -mx-1.5 hover:bg-primary/10 transition-colors translate-y-0.5"
    >
      <span className="relative w-5 h-5 flex-shrink-0">
        <Image 
          src={imageSrc}
          alt={imageAlt}
          fill
          className="rounded object-contain"
        />
      </span>
      {children}
    </a>
  )
}

new_text:
import Image from "next/image"

interface HeroLinkProps {
  href: string
  imageSrc: string
  imageAlt: string
  children: React.ReactNode
}

export function HeroLink({ href, imageSrc, imageAlt, children }: HeroLinkProps) {
  return (
    <a 
      href={href} 
      target="_blank" 
      rel="noopener noreferrer" 
      className="inline-flex items-center gap-2 text-primary hover:text-primary/80 rounded-full px-4 py-1 -mx-2 bg-white border border-primary/20 shadow-sm hover:border-primary/60 transition-all transform hover:scale-105 hover:shadow-md focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-primary/50 focus-visible:ring-offset-2"
      aria-label={typeof children === 'string' ? children : undefined}
      role="link"
    >
      <span className="relative w-6 h-6 flex-shrink-0 flex items-center justify-center">
        <Image 
          src={imageSrc}
          alt={imageAlt}
          fill
          className="rounded object-contain"
        />
      </span>
      <span className="flex items-center">{children}</span>
    </a>
  )
}
